package whileLoop;

public class A2_DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=12;
		do
		{
			System.out.println(i);
		i++;
		}
		while (i<=10);
		{
			System.out.println(i);
			
		}
		System.out.println("condition fail");
		
	}
		
	}

		
	

		
			
		
